package com.day4;

public class stringbuffer {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		StringBuffer k=new StringBuffer("swamy");
		k.append("nayak");
		System.out.println(k);
		k.insert(0,"Ratnavath");
		System.out.println(k);
	//string buffer can posible by only with new keyword ,not posible with literal 

	}

}
//note:we can mutable the string by using stringbuffer method

